mapper.select:
SELECT bookId, bookCateNm, bookCateDepth, bookName, bookDesc, author, bookPrice, stock, stockStatus, publishDate, roleId, cateId, bookImgPath, writer, createDate, recomType, saleStatus FROM book
ORDER BY bookId DESC
LIMIT #{pagination.pageSize} OFFSET #{pagination.offset}

mapper.update:
UPDATE book
SET stock = stock + #{quantity}
WHERE bookId = #{bookId}

UPDATE book
SET stock = stock - #{quantity}
WHERE bookId = #{bookId}
  AND stock >= #{quantity}
