mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END AS isDuplicate
FROM client
WHERE clientId = #{clientId}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END AS isDuplicate
FROM client
WHERE  tel = #{tel}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END AS isDuplicate
FROM client
WHERE  email = #{email}

SELECT EXISTS (
    SELECT 1
    FROM staff
    WHERE staffId = #{staffId}
      AND (staffEmail = #{staffEmail}
        OR staffTelNum = #{staffTelNum})
) AS exists
