mapper.update:
UPDATE client
SET password = #{password}
WHERE clientId = #{clientId}

UPDATE client

    tel = #{tel}
    , zoneCode = #{zoneCode}
    , addr = #{addr}
    , detailAddr = #{detailAddr}
    , email = #{email}

WHERE clientId = #{clientId}
AND (
(tel != #{tel} OR tel IS NULL)
OR (zoneCode != #{zoneCode} OR zoneCode IS NULL)
OR (addr != #{addr} OR addr IS NULL)
OR (detailAddr != #{detailAddr} OR detailAddr IS NULL)
OR (email != #{email} OR email IS NULL)
)
